: #111177\r\n} \r\n\r\nh3 {    \r\n	font-family: Arial, Helvetica, sans-serif;    \r\n	font-size: 18px;    \r\n	color: #111177 \r\n} \r\n\r\nh5 {    \r\n	font-family: Arial, Helvetica, sans-serif;    \r\n	font-size: 12px;    \r\n	color: #111177 \r\n}  \r\n\r\n.tabla_de_foro {\r\n	border:1px;\r\n	border-style:solid;\r\n	border-color: #EEFCFC;\r\n	border-spacing:0px;\r\n	background-color:transparent;\r\n}\r\n\r\n.tabla_de_temas {\r\n	border:1px;\r\n	border-style:solid;\r\n	border-color: #ADCCDC;\r\n	background-color:transparent;\r\n}\r\n\r\n.tabla_de_temas_lista {\r\n	border:1px;\r\n	border-style:solid;\r\n	border-color: #ADCCDC;\r\n	background-color:transparent;\r\n}\r\n\r\n.encabezado_de_foro {\r\n	font-family: Arial, Helvetica, sans-serif;\r\n	font-size: 20px;\r\n	color: black;\r\n	background-color:#ADCCDC;\r\n}\r\n\r\n.encabezado_de_temas {\r\n	font-family: Arial, Helvetica, sans-serif;\r\n	font-size: 14px;\r\n	font-weight:bold;\r\n	color: black;\r\n	background-color:#ACC8D5;\r\n}\r\n\r\n.fila_de_tabla {\r\n	font-family: Arial, Helvetica, sans-serif;\r\n	font-size: 12px;\r\n	color: #111177;\r\n	background-color:#C1D9E6;\r\n}\r\n\r\n.campo {\r\n	font-family: Arial, Helvetica, sans-serif;\r\n	font-size: 12px;\r\n	color: black;\r\n	background-color:transparent;	\r\n}\r\n\r\n.campo_oblig {\r\n	font-family: Arial, Helvetica, sans-serif;\r\n	font-size: 12px;\r\n	font-weight: bold;\r\n	color: black;\r\n	background-color:transparent;	\r\n}\r\n\r\n.valor {\r\n	font-family: Arial, Helvetica, sans-serif;\r\n	font-size: 14px;\r\n	text-decoration: bold;\r\n	color: #111177;\r\n	background-color:transparent;\r\n}", "estiloForo.css", "2");
INSERT INTO ficheros VALUES("29", "foro publico foro", "<?php\r\n	//INCLUIMOS EL FICHERO CON LA LIBRERÕA DEL FORO\r\n	include \"includes/funciones.inc.php\";\r\n	//Y LA DE CONFIGURACI”N DEL MISMO\r\n	include \"includes/config.inc.php\";\r\n	\r\n	global $autor_mensaje, $formato_foro, $permitir_filtro_foro, $permitir_varios_foros, $permitir_paginado_foro, \r\n			$num_mensajes_x_pagina_foro, $permitir_buscador_foro, $mostrar_usuarios_conectados_foro;\r\n\r\n	if ($mostrar_usuarios_conectados_foro) {	\r\n		//INCLUIMOS E INICIALIZAMOS LA CLASE PARA LOS USUARIOS ONLINE\r\n		include(\'includes/usuariosConectados.class.inc.php\');\r\n		$ol = new UsuariosConectados();\r\n	}\r\n	\r\n	//RECUPERAMOS LAS VARIABLES GET Y POST\r\n	$id_tema = @$HTTP_GET_VARS[\'id\'];\r\n	$padre = @$HTTP_GET_VARS[\'id_mensaje\'];\r\n	$titulo = @$HTTP_GET_VARS[\'titulo\'];\r\n	$pag = @$HTTP_GET_VARS[\'pag\'];\r\n	\r\n	if ($permitir_filtro_foro) {	\r\n		//FILTRAMOS Y ORDENAMOS, en su caso\r\n		$filtro = @$HTTP_POST_VARS[\'filtro\'];\r\n		if (!$filtro)\r\n			$filtro = @$HTTP_GET_VARS[\'filtro\'];\r\n		$orden = @$HTTP_POST_VARS[\'orden\'];\r\n		if (!$orden)\r\n			$orden = @$HTTP_GET_VARS[\'orden\'];\r\n		$tabla = array();\r\n		$tabla = filtroYordenacion($filtro, $orden);\r\n	}\r\n	\r\n	if ($mostrar_usuarios_conectados_foro) {\r\n		//OBTENEMOS LA IP O M¡QUINA DEL CLIENTE PARA PRESENTARLA\r\n		$cliente_host = gethostbyaddr(\"$REMOTE_ADDR\"); //Obtiene la IP del cliente\r\n	}\r\n	\r\n	//IMPRIMIMOS EL ENCABEZADO DE P¡GINA\r\n	imprime_encabezado_de_pagina(\"Foro $titulo\",\"\", false);\r\n	\r\n	if ($permitir_iconos_como_enlaces)\r\n		$tam_enlaces = 75;\r\n	else\r\n		$tam_enlaces = 250;\r\n	\r\n?>\r\n<!-- Principio de la fila de enlaces -->\r\n	<tr>\r\n      <td class=\"encabezado_de_foro\">\r\n		<table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n          <tr>\r\n            <td class=\"encabezado_de_foro\"><?=$titulo;?></td>\r\n            <td width=\"<?=$tam_enlaces?>\">\r\n			  <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n                <tr>\r\n				<?php if ($permitir_iconos_como_enlaces) { ?>\r\n					<?php if ($permitir_varios_foros) { ?>\r\n                  		<td><a href=\"index.php\"><img src=\"img/volver1.gif\" alt=\"Volver a la lista tem&aacute;tica\" width=\"20\" height=\"20\"></a></td>\r\n				  	<?php } ?>\r\n                  		<td align=\"<?php if ($permitir_buscador_foro) echo \"center\"; else echo \"right\"; ?>\"><a href=\"escribir.php?id=<?=$id_tema;?>&titulo=<?=$titulo?>&padre=0\"><img src=\"img/nuevo.gif\" alt=\"Nuevo mensaje\" width=\"20\" height=\"20\"></a></td>\r\n				  	<?php if ($permitir_buscador_foro) { ?>\r\n                  		<td align=\"right\"><a href=\'buscar.php?id=<?=$id_tema?>&titulo=<?=$titulo?>&pag=<?=$pag?>&filtro=<?=$filtro?>&orden=<?=$orden?>\'><img src=\"img/buscar.gif\" alt=\"Buscar\" width=\"20\" height=\"20\"></a></td>\r\n				  <?php } ?>\r\n				<?php } else { ?>\r\n					<?php if ($permitir_varios_foros) { ?>\r\n                  		<td><a href=\"index.php\">Lista tem&aacute;tica</a></td>\r\n				  	<?php } ?>\r\n                  		<td align=\"<?php if ($permitir_buscador_foro) echo \"center\"; else echo \"right\"; ?>\"><a href=\"escribir.php?id=<?=$id_tema;?>&titulo=<?=$titulo?>&padre=0\">Nuevo mensaje</a></td>\r\n				  	<?php if ($permitir_buscador_foro) { ?>\r\n                  		<td align=\"right\"><a href=\'buscar.php?id=<?=$id_tema?>&titulo=<?=$titulo?>&pag=<?=$pag?>&filtro=<?=$filtro?>&orden=<?=$orden?>\'>Buscar</a></td>\r\n				  <?php } ?>\r\n				<?php } ?>\r\n                </tr>\r\n              </table></td>\r\n          </tr>\r\n        </table></td>\r\n    </tr>\r\n<!-- Fin de la fila de enlaces -->\r\n<!-- Principio de la fila de los filtros -->\r\n	<?php if ($permitir_filtro_foro || $mostrar_usuarios_conectados_foro) { ?>\r\n	<tr>\r\n      <td class=\"encabezado_de_foro\">\r\n		<table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n          <tr>\r\n		  <?php if ($permitir_filtro_foro) { ?>\r\n          <form name=\"filtrar\" action=\"foro.php?id=<?=$id_tema?>&titulo=<?=$titulo?>&pag=<?=$pag?>\" enctype=\"multipart/form-data\" method=\"post\">\r\n	        <td class=\"campo\">Mostrar: \r\n	          <select name=\"filtro\" class=\"datos_form\">\r\n	          	<option value=\"hoy\" <?php if ($filtro==\"hoy\") echo \"selected\";?>>S&oacute;lo las de hoy</option>\r\n	          	<option value=\"semana\" <?php if ($filtro==\"semana\") echo \"selected\";?>>&Uacute;ltima semana</option>\r\n	          	<option value=\"mes\" <?php if ($filtro==\"mes\") echo \"selected\";?>>&Uacute;ltimo mes</option>\r\n	          	<option value=\"anyo\" <?php if ($filtro==\"anyo\") echo \"selected\";?>>&Uacute;ltimo a&ntilde;o</option>\r\n	          	<option value=\"todas\" <?php if (!$filtro || $filtro==\"todas\") echo \"selected\";?>>Todas</option>\r\n	          </select>\r\n          , ordenados por: \r\n          <select name=\"orden\" class=\"datos_form\">\r\n            <option value=\"asunto\" <?php if ($orden==\"asunto\") echo \"selected\";?>>Asunto</option>\r\n			<?php if ($formato_foro == \"tabla\" && $autor_mensaje) { ?>\r\n            <option value=\"autor\" <?php if ($orden==\"autor\") echo \"selected\";?>>Autor</option>\r\n			<?php } ?>\r\n            <option value=\"fecha_publicacion\" <?php if (!$orden || $orden==\"fecha_publicacion\") echo \"selected\";?>>Fecha</option>\r\n			<?php if ($formato_foro == \"tabla\") { ?>\r\n            <option value=\"lecturas\" <?php if ($orden==\"lecturas\") echo \"selected\";?>>Lecturas</option>\r\n            <option value=\"respuestas\" <?php if ($orden==\"respuestas\") echo \"selected\";?>>Respuestas</option>\r\n            <option value=\"ultima_resp\" <?php if ($orden==\"ultima_resp\") echo \"selected\";?>>&Uacute;ltima respuesta</option>\r\n			<?php } ?>\r\n          </select>\r\n	          <input type=\"submit\" name=\"Submit\" value=\"Aplicar\" class=\"boton\">\r\n        </td></form>\r\n		<?php } if ($mostrar_usuarios_conectados_foro) { ?>\r\n			 <td align=\"right\" class=\"campo\">Usuario: </font><font class=\"valor\"><?=$cliente_host?></font> | Total conectados: </font><font class=\"valor\"><?=$ol->get_num_usuarios()?></font><font class=\"campo\"></td>\r\n		<?php } ?>\r\n          </tr>\r\n        </table></td>\r\n    </tr>\r\n	<?php } ?>\r\n<!-- Fin de la fila de los filtros -->\r\n<?php\r\n	if ($permitir_paginado_foro) {\r\n		//Calculamos los datos necesarios para realizar la paginaciÛn\r\n		$limite_inf = ($pag * $num_mensajes_x_pagina_foro) - $num_mensajes_x_pagina_foro;\r\n		$limite_sup = (($pag + 1) * $num_mensajes_x_pagina_foro) - $num_mensajes_x_pagina_foro ;\r\n		\r\n		//Creamos la sentencia sql y consultamos seg˙n el filtro si se da el caso\r\n		$sql = \"select id_mensaje, asunto, fecha_publicacion, lecturas, num_respuestas, fecha_ultima_respuesta from mensajes where id_tema=\'$id_tema\' and id_padre=\'0\'\";\r\n		if ($permitir_filtro_foro) $sql .= \" \".$tabla[\'filtro\'].\" \";\r\n		$sql .= \"order by fecha_publicacion DESC\";\r\n		$res = consulta ($sql);\r\n		\r\n		$num_mensajes_total = @mysql_num_rows($res);\r\n		$num_paginas = (int)(($num_mensajes_total-1) / $num_mensajes_x_pagina_foro) + 1;\r\n		@mysql_free_result($res);\r\n	}\r\n	\r\n	//Creamos la sentencia sql de consulta con el filtro y el orden en su caso\r\n	$sql = \"select id_mensaje, asunto,\";\r\n	if ($autor_mensaje) $sql .= \" autor,\";\r\n	$sql .= \" fecha_publicacion, lecturas, num_respuestas, fecha_ultima_respuesta from mensajes where id_tema=\'$id_tema\' and id_padre=\'0\'\";\r\n	if ($permitir_filtro_foro) $sql .= \" \".$tabla[\'filtro\'].\" \".$tabla[\'orden\'].\" \";\r\n	if ($permitir_paginado_foro) $sql .= \"LIMIT $limite_inf,$num_mensajes_x_pagina_foro\";\r\n	\r\n	//Creamos la fila con la lista de mensajes del foro\r\n	if (!isset($filtro)) $filtro = \"\";\r\n	if (!isset($orden)) $orden = \"\";\r\n	listar_mensajes($sql, $id_tema, $titulo, $pag, $filtro, $orden, false);\r\n\r\n	if ($permitir_paginado_foro) {\r\n		//Creamos la fila de la paginaciÛn\r\n		crear_paginacion($limite_inf, $limite_sup, $num_paginas, $num_mensajes_total, $id_tema, $titulo, $pag, $filtro, $orden);\r\n	}\r\n	\r\n	//Imprimimos el pie de p·gina\r\n	imprime_pie_de_pagina();\r\n?>", "foro.php", "1");
INSERT INTO ficheros VALUES("30", "foro publico index", "<?php \r\n	include \"includes/funciones.inc.php\";\r\n	include \"includes/config.inc.php\";\r\n	\r\n	global $permitir_varios_foros, $titulo_foro_unico, $nombre_del_foro, $permitir_iconos_como_enlaces;\r\n\r\n	if ($permitir_varios_foros) {\r\n		//Encabezado de p·gina\r\n		imprime_encabezado_de_pagina(\"Lista de foros\",\"\", false);\r\n?>\r\n	<tr><td class=\"encabezado_de_foro\">\r\n		<table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n			<tr>\r\n				<td class=\"encabezado_de_foro\"><?=htmlentities($nombre_del_foro)?></td>\r\n				<?php if ($permitir_iconos_como_enlaces) { ?>\r\n					<td class=\"encabezado_de_foro\" align=\"center\" width=\"25\"><a href=\"admin/\"><img src=\"img/admin.gif\" alt=\"Administrador\" width=\"20\" height=\"20\"></a></td>\r\n					<td align=\"right\" width=\"25\"><a href=\"tema.php\"><img src=\"img/nuevo.gif\" alt=\"Nuevo tema\" width=\"20\" height=\"20\"></a></td>\r\n				<?php } else { ?>\r\n					<td class=\"encabezado_de_foro\" align=\"center\" width=\"50\"><a href=\"admin/\">Administrador</a></td>\r\n					<td align=\"right\" width=\"80\"><a href=\"tema.php\">Nuevo tema</a></td>\r\n				<?php } ?>\r\n			</tr>\r\n		</table>\r\n	</td></tr>\r\n<?php\r\n		//Crea la fila con la lista de foros\r\n		listar_foros(false);\r\n\r\n		//Pie de p·gina\r\n		imprime_pie_de_pagina();\r\n	} else {\r\n		header (\"Location: foro.php?id=1&titulo=$titulo_foro_unico&pag=1\");\r\n		exit();\r\n	}\r\n?>", "index.php", "1");
INSERT INTO ficheros VALUES("31", "foro publico instalar", "<?php \r\n	include \"includes/funciones.inc.php\";\r\n	include \"includes/config.inc.php\";\r\n	\r\n	//ESTAS DOS VARIABLES DEBEN SER SOLICITADAS MEDIANTES FORMULARIOS CON LOS DATOS DE ENTRADA DE MYSQL\r\n	//$login_administrador = \"josema\";\r\n	//$password_administrador = \"josema\";\r\n	\r\n	global $basedatos, $permitir_varios_foros, $nombre_del_foro, $permitir_valoraciones_foro, $mostrar_usuarios_conectados_foro,\r\n			$autor_mensaje, $email_mensaje, $web_mensaje, $imagen_mensaje;\r\n\r\n	imprime_encabezado_de_pagina(\'Instalaci&oacute;n del foro\', \'\');\r\n	\r\n	echo \"<tr><td class=\\\"encabezado_de_foro\\\">INSTALACI&Oacute;N DEL FORO \'\".strtoupper($nombre_del_foro).\"\'...</td></tr>\";\r\n	\r\n	//CREACI”N DE LA \'BASE DE DATOS\'\r\n	$sql = \"CREATE DATABASE $basedatos\";\r\n	if (consulta($sql))\r\n		echo \"<tr><td class=\\\"tabla_de_temas\\\">La base de datos \".strtoupper($basedatos).\" ha sido creada con &eacute;xito.</td></tr>\";\r\n	else {\r\n		echo \"<tr><td class=\\\"tabla_de_temas\\\">ERROR al crear la base de datos \".strtoupper($basedatos).\".</td></tr>\";\r\n		echo \"<tr><td class=\\\"fila_de_tabla\\\">\".nl2br(htmlentities($sql)).\"</td></tr>\";\r\n	}\r\n	\r\n	//CREACI”N DE LA TABLA DEL \'ADMINISTRADOR\'\r\n	$sql = \"CREATE TABLE administrador (\r\n  				id_administrador tinyint(3) unsigned NOT NULL auto_increment,\r\n  				login varchar(25) NOT NULL default \'\',\r\n  				password varchar(32) NOT NULL default \'\',\r\n  				PRIMARY KEY  (id_administrador),\r\n  				UNIQUE KEY login (login))\";\r\n	if (consulta($sql))\r\n		echo \"<tr><td class=\\\"tabla_de_temas\\\">La tabla ADMINISTRADOR se ha creado con &eacute;xito.</td></tr>\";\r\n	else {\r\n		echo \"<tr><td class=\\\"tabla_de_temas\\\">ERROR al crear la tabla ADMINISTRADOR.</td></tr>\";\r\n		echo \"<tr><td class=\\\"fila_de_tabla\\\">\".nl2br(htmlentities($sql)).\"</td></tr>\";\r\n	}\r\n	\r\n	//CREACI”N DEL USUARIO ADMINISTRADOR POR DEFECTO\r\n	$sql = \"INSERT INTO administrador(login,password) VALUES (\'$login_administrador\', \'\".md5($password_administrador).\"\')\";\r\n	if (consulta($sql))\r\n		echo \"<tr><td class=\\\"tabla_de_temas\\\">Se ha dado de alta al usuario administrador con &eacute;xito.</td></tr>\";\r\n	else {\r\n		echo \"<tr><td class=\\\"tabla_de_temas\\\">ERROR al dar de alta al usuario administrador.</td></tr>\";\r\n		echo \"<tr><td class=\\\"fila_de_tabla\\\">\".nl2br(htmlentities($sql)).\"</td></tr>\";\r\n	}\r\n	\r\n	if ($mostrar_usuarios_conectados_foro) {\r\n		//CREACI”N DE LA TABLA DE \'USUARIOSCONECTADOS\'\r\n		$sql = \"CREATE TABLE usuariosconectados (\r\n			  		tiempoConexion int(15) NOT NULL default \'0\',\r\n					ip varchar(40) NOT NULL default \'\',\r\n			  		archivo varchar(100) NOT NULL default \'\',\r\n			  		KEY tiempoConexion (tiempoConexion),\r\n			  		KEY ip (ip),\r\n			  		KEY archivo (archivo))\";\r\n		if (consulta($sql))\r\n			echo \"<tr><td class=\\\"tabla_de_temas\\\">La tabla USUARIOSCONECTADOS se ha creado con &eacute;xito.</td></tr>\";\r\n		else {\r\n			echo \"<tr><td class=\\\"tabla_de_temas\\\">ERROR al crear la tabla USUARIOSCONECTADOS.</td></tr>\";\r\n			echo \"<tr><td class=\\\"fila_de_tabla\\\">\".nl2br(htmlentities($sql)).\"</td></tr>\";\r\n		}\r\n	}\r\n	\r\n	if ($permitir_varios_foros) {\r\n		//CREACION DE LA TABLA DE \'TEMAS\' DEL FORO\r\n		$sql = \"CREATE TABLE temas (\r\n					  id_tema int(7) unsigned NOT NULL auto_increment,\r\n					  titulo_tema varchar(255) NOT NULL default \'\',\r\n					  descripcion varchar(255) NOT NULL default \'\',\r\n					  PRIMARY KEY  (id_tema),\r\n					  UNIQUE KEY id_tema (id_tema,titulo_tema),\r\n					  KEY id_tema_2 (id_tema))\";\r\n		if (consulta($sql))\r\n			echo \"<tr><td class=\\\"tabla_de_temas\\\">La tabla TEMAS se ha creado con &eacute;xito.</td></tr>\";\r\n		else {\r\n			echo \"<tr><td class=\\\"tabla_de_temas\\\">ERROR al crear la tabla TEMAS.</td></tr>\";\r\n			echo \"<tr><td class=\\\"fila_de_tabla\\\">\".nl2br(htmlentities($sql)).\"</td></tr>\";\r\n		}\r\n	}\r\n		\r\n	//CREACI”N DE LA TABLA DE \'MENSAJES\'\r\n	$sql = \"CREATE TABLE mensajes (\r\n				  id_mensaje int(11) unsigned NOT NULL auto_increment,\r\n				  id_tema int(7) unsigned NOT NULL default \'0\',\r\n				  id_padre int(11) unsigned NOT NULL default \'0\',\r\n				  asunto varchar(255) NOT NULL default \'\',\";\r\n	if ($autor_mensaje) {\r\n		$sql .= \" autor varchar(255) NOT NULL default \'\',\";\r\n	}\r\n	if ($email_mensaje) {\r\n		$sql .= \" autor_email varchar(255) NOT NULL default \'\',\";\r\n	}\r\n	$sql .= \"	  autor_host varchar(255) NOT NULL default \'\',\r\n				  descripcion text NOT NULL,\r\n				  fecha_publicacion datetime NOT NULL default \'0000-00-00 00:00:00\',\r\n				  num_respuestas int(7) unsigned NOT NULL default \'0\',\r\n				  fecha_ultima_respuesta datetime NOT NULL default \'0000-00-00 00:00:00\',\";\r\n	if ($web_mensaje){\r\n		$sql .= \" web varchar(255) NOT NULL default \'\',\";\r\n	}\r\n	$sql .= \"	  lecturas int(7) unsigned NOT NULL default \'0\',\";\r\n	if ($imagen_mensaje) {\r\n		$sql .= \" imagen_data longblob NOT NULL,\r\n				  imagen_name varchar(255) NOT NULL default \'\',\r\n				  imagen_type varchar(50) NOT NULL default \'\',\";\r\n	}\r\n	if ($permitir_valoraciones_foro) {\r\n		$sql .= \" valoracion_total double NOT NULL default \'0\',\r\n				  num_valoraciones int(4) unsigned NOT NULL default \'0\',\";\r\n	}\r\n	$sql .= \"	  PRIMARY KEY  (id_mensaje),\r\n				  UNIQUE KEY id_mensaje (id_mensaje),\r\n				  KEY id_mensaje_2 (id_mensaje))\";\r\n	if (consulta($sql))\r\n		echo \"<tr><td class=\\\"tabla_de_temas\\\">La tabla MENSAJES se ha creado con &eacute;xito.</td></tr>\";\r\n	else {\r\n		echo \"<tr><td class=\\\"tabla_de_temas\\\">ERROR al crear la tabla MENSAJES.</td></tr>\";\r\n		echo \"<tr><td class=\\\"fila_de_tabla\\\">\".nl2br(htmlentities($sql)).\"</td></tr>\";\r\n	}\r\n		\r\n	imprime_pie_de_pagina();\r\n	require(\"estiloForo.css.php\");\r\n?>", "instalar.php", "1");
INSERT INTO ficheros VALUES("32", "foro publico mensaje", "<?php\r\n	include \"includes/funciones.inc.php\";\r\n	include \"includes/config.inc.php\";\r\n	\r\n	global $autor_mensaje ,$email_mensaje, $web_mensaje, $imagen_mensaje, $permitir_codigo_html, $permitir_valoraciones_foro;\r\n	\r\n	$id_tema = @$HTTP_GET_VARS[\'id\'];\r\n	$id_mensaje = @$HTTP_GET_VARS[\'id_mensaje\'];\r\n	$titulo = @$HTTP_GET_VARS[\'titulo\'];\r\n	$pag = @$HTTP_GET_VARS[\'pag\'];\r\n	$filtro = @$HTTP_GET_VARS[\'filtro\'];\r\n	$orden = @$HTTP_GET_VARS[\'orden\'];\r\n	\r\n	if ($id_mensaje) {\r\n		if ($permitir_valoraciones_foro) {\r\n			//Comprobamos si se est· realizando una votaciÛn, en cuyo caso actualizamos la media aÒadiendo el voto\r\n			if (@$HTTP_POST_VARS[\'voto\']) {\r\n				$num_v = @$HTTP_POST_VARS[\'valoraciones\'];\r\n				$val_t = @$HTTP_POST_VARS[\'valoracion\'];\r\n				$voto = @$HTTP_POST_VARS[\'voto\'];\r\n				$valoracion_total = (($val_t * $num_v) + $voto) / ($num_v + 1);\r\n				$sql = \"UPDATE mensajes SET num_valoraciones=\'$num_v\'+1, valoracion_total=\'$valoracion_total\' \r\n						WHERE id_mensaje=\'$id_mensaje\' AND id_tema=\'$id_tema\'\";\r\n				consulta($sql);\r\n			} else {\r\n				//En caso contrario, actualizamos las lecturas de este mensaje\r\n				consulta(\"UPDATE mensajes SET lecturas=lecturas+1 WHERE id_mensaje=\'$id_mensaje\' AND id_tema=\'$id_tema\'\");\r\n			}\r\n		} else {\r\n			//En caso contrario, actualizamos las lecturas de este mensaje\r\n			consulta(\"UPDATE mensajes SET lecturas=lecturas+1 WHERE id_mensaje=\'$id_mensaje\' AND id_tema=\'$id_tema\'\");\r\n		}\r\n		\r\n		//Consultamos los datos del mensaje\r\n		$sql = \"SELECT id_mensaje, id_padre, num_respuestas, asunto,\";\r\n		if ($autor_mensaje) $sql .= \" autor,\";\r\n		if ($email_mensaje) $sql .= \" autor_email,\";\r\n		if ($web_mensaje) $sql .= \" web,\";\r\n		if ($imagen_mensaje) $sql .= \" imagen_name,\";\r\n		if ($permitir_valoraciones_foro) $sql .= \" valoracion_total, num_valoraciones,\";\r\n		$sql .= \"descripcion, fecha_publicacion, lecturas FROM mensajes WHERE id_mensaje=\'$id_mensaje\' AND id_tema=\'$id_tema\'\";\r\n		$res = consulta ($sql);\r\n	}\r\n	\r\n	//Comprobamos si tiene padre\r\n	if ($reg = @mysql_fetch_array($res)) {\r\n		$id_padre = $reg [\'id_padre\'];\r\n		$asunto = $reg[\'asunto\'];\r\n	}\r\n	\r\n	if ($permitir_iconos_como_enlaces) {\r\n		if ($id_padre!=\"0\")\r\n			$tam_enlaces = \"75\";\r\n		else\r\n			$tam_enlaces = \"50\";\r\n	} else {\r\n		if ($id_padre!=\"0\")\r\n			$tam_enlaces = \"250\";\r\n		else\r\n			$tam_enlaces = \"170\";\r\n	}\r\n		\r\n	imprime_encabezado_de_pagina(\"Mensajes del foro $titulo\",\"\", false);\r\n?>\r\n<!-- Principio de la fila de enlaces -->\r\n<tr>\r\n      <td class=\"encabezado_de_foro\">\r\n		<table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n          <tr>\r\n            <td class=\"encabezado_de_foro\"><?=$titulo;?></td>\r\n            \r\n          <td width=\"<?=$tam_enlaces?>\"> <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n              <tr> \r\n			  <?php if ($permitir_iconos_como_enlaces) { ?>\r\n					<?php if ($id_padre!=\"0\") {\r\n							echo \"                  <td><a href=\\\"javascript:history.back();\\\"><img src=\\\"img/volver1.gif\\\" alt=\\\"Volver atr&aacute;s\\\" width=\\\"20\\\" height=\\\"20\\\"></a></td>\\n\"; //mensaje.php?id=$id_tema&id_mensaje=$id_padre&titulo=$titulo\r\n						  } \r\n					?>\r\n					<td><div align=\"center\"><a href=\"foro.php?id=<?=$id_tema;?>&titulo=<?=$titulo?>&pag=<?=$pag?>&filtro=<?=$filtro?>&orden=<?=$orden?>\"><img src=\"img/volver2.gif\" alt=\"Volver al foro\" width=\"20\" height=\"20\"></a></div></td>\r\n					<td align=\"right\"><a href=\'escribir.php?id=<?=$id_tema?>&titulo=<?=$titulo?>&padre=<?=$id_mensaje?>&asunto=<?=$asunto?>&pag=<?=$pag?>\'><img src=\"img/responder.gif\" alt=\"Responder\" width=\"20\" height=\"20\"></a></td>\r\n				<?php } else { ?>\r\n										<?php if ($id_padre!=\"0\") {\r\n							echo \"                  <td><a href=\\\"javascript:history.back();\\\">Volver atr&aacute;s</a></td>\\n\"; //mensaje.php?id=$id_tema&id_mensaje=$id_padre&titulo=$titulo\r\n						  } \r\n					?>\r\n					<td><div align=\"center\"><a href=\"foro.php?id=<?=$id_tema;?>&titulo=<?=$titulo?>&pag=<?=$pag?>&filtro=<?=$filtro?>&orden=<?=$orden?>\">Volver \r\n						al foro</a></div></td>\r\n					<td align=\"right\"><a href=\'escribir.php?id=<?=$id_tema?>&titulo=<?=$titulo?>&padre=<?=$id_mensaje?>&asunto=<?=$asunto?>&pag=<?=$pag?>\'>Responder</a></td>\r\n				<?php } ?>\r\n              </tr>\r\n            </table></td>\r\n          </tr>\r\n        </table></td>\r\n    </tr>\r\n<!-- Fin de la fila de enlaces -->\r\n<!-- Principio de la fila de DATOS DEL MENSAJE -->\r\n    <tr class=\"fila_de_tabla\">\r\n      <td><table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" class=\"tabla_de_temas\">\r\n          <tr> \r\n            <td height=\"18\"> \r\n              <?php\r\n			  		if ($reg) {\r\n						if ($autor_mensaje) $autor = htmlentities($reg[\'autor\']);\r\n						if ($email_mensaje) $email = htmlentities($reg[\'autor_email\']);\r\n						if ($web_mensaje) $web = htmlentities($reg[\'web\']);\r\n						if ($permitir_codigo_html)\r\n							$descripcion = nl2br($reg[\'descripcion\']);\r\n						else\r\n							$descripcion = nl2br(htmlentities($reg[\'descripcion\']));\r\n						$fecha_publicacion = $reg[\'fecha_publicacion\'];\r\n						$lecturas = $reg[\'lecturas\'];\r\n						if ($permitir_valoraciones_foro) {\r\n							$valoracion_total = $reg[\'valoracion_total\'];\r\n							//construimos el grafico de la valoraciÛn\r\n							$valoracion_img = \"\";\r\n							for ($i=0;$i<(int)$valoracion_total;$i++)\r\n								$valoracion_img.=\"<img src=\\\"img/estrella.gif\\\">\";\r\n							$valoraciones = $reg [\'num_valoraciones\'];\r\n						}\r\n						if ($imagen_mensaje) $imagen_name = $reg [\'imagen_name\'];\r\n			  ?>\r\n              \r\n            <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"2\">\r\n              <tr> \r\n                <td class=\"encabezado_de_temas\"> \r\n                  <?=$asunto;?>\r\n                </td>\r\n                <td class=\"encabezado_de_temas\" align=\"right\"> \r\n                  <?=formatear_fecha($fecha_publicacion)?>\r\n                </td>\r\n              </tr>\r\n              <tr> \r\n              <td colspan=\"2\"><font class=\"valor\"><?=$descripcion?></font><br><br>\r\n			  </td>\r\n              </tr>\r\n			<?php if (($autor_mensaje && $autor) || ($permitir_iconos_como_enlaces && (($email_mensaje && $email) || ($web_mensaje && $web)))) { ?>\r\n              <tr> \r\n                <td colspan=\"2\"><font class=\"campo\">Autor:</font> <font class=\"valor\"> \r\n                  <?=$autor?>\r\n				  <?php if ($permitir_iconos_como_enlaces && ($email_mensaje && $email)) echo \"<a href=\\\"mailto:$email\\\" target=\\\"_blank\\\"><img src=\\\"img/email.gif\\\" alt=\\\"Enviar correo\\\" align=\\\"absmiddle\\\"></a>\"; ?>\r\n				  <?php if ($permitir_iconos_como_enlaces && ($web_mensaje && $web)) echo \"<a href=\\\"$web\\\" target=\\\"_blank\\\"><img src=\\\"img/web.gif\\\" alt=\\\"Web personal\\\" align=\\\"absmiddle\\\"></a>\"; ?>\r\n                  </font> </td>\r\n              </tr>\r\n              <?php } if ($email_mensaje && $email && !$permitir_iconos_como_enlaces) { ?>\r\n              <tr> \r\n                <td colspan=\"2\"><font class=\"campo\">E-mail: </font><font class=\"valor\"> \r\n                  <a href=\"mailto:<?=$email?>\"> \r\n                  <?=$email?>\r\n                  </a> </font></td>\r\n              </tr>\r\n              <?php } if ($web_mensaje && $web && !$permitir_iconos_como_enlaces) { ?>\r\n              <tr> \r\n                <td colspan=\"2\"><font class=\"campo\">Web: </font><font class=\"valor\"> \r\n                  <a href=\"<?=$web?>\" target=\"_blank\"> \r\n                  <?=$web?>\r\n                  </a> </font></td>\r\n              </tr>\r\n              <?php } ?>\r\n              <tr> \r\n                <td colspan=\"2\"><font class=\"campo\">Lecturas: </font><font class=\"valor\"> \r\n                  <?=$lecturas?>\r\n                  </font></td>\r\n              </tr>\r\n			                <?php if ($imagen_mensaje && $imagen_name) { //Si hay una imagen relacionada la presentamos ?>\r\n              <tr> \r\n                <td colspan=\"2\"><font class=\"campo\">Imagen relacionada: </font><font class=\"valor\"><?=$imagen_name?></font>&nbsp;&nbsp;&nbsp;\r\n				<a href=\"consultaImagen.php?id_mensaje=<?=$id_mensaje?>&ver=si\"> [ver] </a>\r\n				<a href=\"consultaImagen.php?id_mensaje=<?=$id_mensaje?>&ver=no\"> [Descargar] </a>\r\n                </td>\r\n              </tr>\r\n              <?php } //fin de la presentaciÛn de la imagen ?>\r\n			  <?php if ($permitir_valoraciones_foro) { ?>\r\n              <tr> \r\n                <td><font class=\"campo\">Valoraci&oacute;n: </font><font class=\"valor\"> \r\n                  <?php if ($valoracion_total) echo $valoracion_img.\" ($valoraciones votos)\"; else echo \"Este mensaje no ha sido valorado\"; ?>\r\n                  </font></td>\r\n                <td width=\"200\">\r\n				<?php if (!@$HTTP_POST_VARS[\'voto\']) { ?>\r\n				<form name=\"valorar\" action=\"mensaje.php?id=<?=$id_tema?>&id_mensaje=<?=$id_mensaje?>&titulo=<?=$titulo?>&pag=<?=$pag?>\" enctype=\"multipart/form-data\" method=\"post\">\r\n                    Valora este mensaje<br>\r\n					<input type=\"hidden\" name=\"valoracion\" value=\"<?=$valoracion_total?>\">\r\n					<input type=\"hidden\" name=\"valoraciones\" value=\"<?=$valoraciones?>\">\r\n					<select name=\"voto\">\r\n                      <option value=\"1\">1 (Nada interesante)</option>\r\n                      <option value=\"2\">2</option>\r\n                      <option value=\"3\">3</option>\r\n                      <option value=\"4\">4</option>\r\n                      <option value=\"5\">5</option>\r\n                      <option value=\"6\">6</option>\r\n                      <option value=\"7\">7</option>\r\n                      <option value=\"8\">8</option>\r\n                      <option value=\"9\">9</option>\r\n                      <option value=\"10\" selected>10 (Muy interesante)</option>\r\n                    </select>\r\n                    <input type=\"button\" name=\"votar\" value=\"Votar\" onClick=\"javascript:document.valorar.submit();\" class=\"boton\">\r\n                </form>\r\n				<?php } ?>\r\n				</td>\r\n              </tr>\r\n			  <?php } ?>\r\n            </table> \r\n			  <?php\r\n					}\r\n					@mysql_free_result($res);\r\n			   ?>\r\n            </td>\r\n          </tr>\r\n        </table></td>\r\n    </tr>\r\n<!-- Fin de la fila de DATOS DEL MENSAJE -->\r\n<?php\r\n	//Consulta de las respuestas de este mensaje\r\n	$sql = \"SELECT id_mensaje, asunto, \";\r\n	if ($autor_mensaje) $sql .= \"autor, \";\r\n	$sql .= \"fecha_publicacion, lecturas, num_respuestas, fecha_ultima_respuesta FROM mensajes WHERE id_padre=\'$id_mensaje\' AND id_tema=\'$id_tema\' ORDER BY fecha_publicacion DESC\";\r\n\r\n	//Creamos la tabla de respuestas del mensaje\r\n	listar_mensajes($sql, $id_tema, $titulo, $pag, $filtro, $orden, false);\r\n\r\n	//Pie de p·gina\r\n	imprime_pie_de_pagina();\r\n?>\r\n                ", "mensaje.php", "1");
INSERT INTO ficheros VALUES("33", "foro publico tema", "<?php\r\n	include \"includes/funciones.inc.php\";\r\n	\r\n	//Si se ha realizado submit grabamos los datos del nuevo tema en la base de datos\r\n	if (@$HTTP_POST_VARS) {\r\n		//Recogemos los datos del formulario	\r\n		$tema = @$HTTP_POST_VARS[\'tema\'];\r\n		$descripcion = @$HTTP_POST_VARS[\'descripcion\'];\r\n		\r\n		//Creamos la sentencia sql y la ejecutamos para dar de alta el nuevo mensaje\r\n		$sql = \"INSERT INTO temas (titulo_tema, descripcion) VALUES (\'$tema\', \'$descripcion\')\";\r\n		consulta ($sql);\r\n		\r\n		//Volvemos al Ìndice de temas\r\n		header (\"Location: index.php\");\r\n		exit;\r\n	}\r\n	$javascript = \"\r\n<script language=\\\"JavaScript\\\" src=\\\"includes/funciones.js\\\" type=\\\"text/javascript\\\"></script>\r\n<script language=\\\"JavaScript\\\" type=\\\"text/javascript\\\">\r\n<!--\r\nfunction comprobarFormulario(form) {\r\n	//Comprueba si se ha introducido asunto\r\n	if (form.tema.value==\\\"\\\") {\r\n		alert (\\\"No puedes dejar el tema vacÌo\\\");\r\n		form.tema.focus();\r\n		return (false);\r\n	}\r\n		\r\n	//Comprueba si se ha introducido la descripciÛn\r\n	if (form.descripcion.value==\\\"\\\") {\r\n		alert (\\\"No puedes dejar la descripciÛn del tema vacÌo\\\");\r\n		form.descripcion.focus();\r\n		return (false);\r\n	}\r\n	\r\n	form.submit();\r\n	return (true);\r\n}\r\n\r\n//-->\r\n</script>\\n\";\r\n	\r\n	//Encabezado de p·gina\r\n	imprime_encabezado_de_pagina(\"Escribir un nuevo tema en el foro\", $javascript, false);\r\n?>\r\n<!-- Principio de la fila de enlaces -->\r\n    <tr> \r\n      <td class=\"encabezado_de_foro\"> <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n          <tr> \r\n            <td class=\"encabezado_de_foro\">\r\n              ESCRIBIR UN NUEVO TEMA\r\n            </td>\r\n            <td width=\"150\"> <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n                <tr>\r\n				<?php if ($permitir