INSERT INTO ficheros VALUES("60", "foro protegido", "<?php\r\n//session_start();\r\n\r\n /****************************************************\r\n        M”DULO GENERAL DE FUNCIONES EN PHP\r\n        ----------------------------------\r\n  Este mÛdulo contiene funciones ˙tiles que se usan en\r\n  la mayorÌa de las p·ginas de nuestra aplizaciÛn.\r\n  A continuaciÛn describiremos cada funciÛn de forma\r\n  detallada:\r\n ****************************************************/\r\n\r\nrequire \"datos_conexion.inc.php\";\r\ninclude \"config.inc.php\";\r\n\r\n/****************************************************\r\n La siguiente funciÛn realiza una consulta sobre la\r\n base de datos, dada una sentencia sql que se le pasa\r\n como par·metro. Utiliza para ello las variables\r\n globales de conexiÛn a la base de datos.\r\n ****************************************************/\r\nfunction consulta ($consulta_sql) {\r\n	global $basedatos, $host, $usuario, $password;\r\n\r\n	//accedemos a la base de datos\r\n	$conexion = mysql_connect($host, $usuario, $password);\r\n	mysql_select_db($basedatos,$conexion);\r\n        	\r\n	//realizamos la consulta de la tabla\r\n	$res=mysql_query($consulta_sql,$conexion);\r\n        	\r\n	//devolvemos el resultado de la consulta\r\n	mysql_close($conexion);\r\n	return $res;\r\n}\r\n\r\n/****************************************************\r\n La siguiente funciÛn formatea una fecha de entrada\r\n de la forma siguiente:\'dia\'-\'mes\'-\'aÒo\' | \'hora\'\r\n ****************************************************/\r\nfunction formatear_fecha ($fecha_ent) {\r\n	$fecha_sal = \"\";\r\n	\r\n	//Separamos la fecha y la hora de la entrada\r\n	$separar_fecha_hora = @explode(\' \', $fecha_ent);\r\n	$fecha = $separar_fecha_hora[0];\r\n	$hora = $separar_fecha_hora[1];\r\n	\r\n	//Recogemos los datos de la fecha de entrada\r\n	$fecha = @explode(\'-\', $fecha);\r\n	\r\n	//Formateamos la fecha y la devolvemos\r\n	$fecha_sal = (int)$fecha[2].\"-\".(int)$fecha[1].\"-\".$fecha[0].\" | $hora\";\r\n	return $fecha_sal;\r\n}\r\n\r\n/****************************************************\r\n La siguiente funciÛn devuelve el mes dado el n˙mero\r\n ****************************************************/\r\nfunction mes_cadena($mes_numero){\r\n	switch($mes_numero){\r\n		case(1):$meshoy=\"Enero\";break;\r\n		case(2):$meshoy=\"Febrero\";break;\r\n		case(3):$meshoy=\"Marzo\";break;\r\n		case(4):$meshoy=\"Abril\";break;\r\n		case(5):$meshoy=\"Mayo\";break;\r\n		case(6):$meshoy=\"Junio\";break;\r\n		case(7):$meshoy=\"Julio\";break;\r\n		case(8):$meshoy=\"Agosto\";break;\r\n		case(9):$meshoy=\"Septiembre\";break;\r\n		case(10):$meshoy=\"Octubre\";break;\r\n		case(11):$meshoy=\"Noviembre\";break;\r\n		case(12):$meshoy=\"Diciembre\";break;\r\n		default:$meshoy=\"\";break;\r\n	}	\r\n	return $meshoy;\r\n}\r\n\r\n/****************************************************\r\n La siguiente funciÛn escribe la tabla formateada de\r\n una consulta de un conjunto de mensajes del foro.\r\n ****************************************************/\r\nfunction listar_mensajes($sql, $id_tema, $titulo, $pag, $filtro, $orden, $es_acceso_administrador) {\r\n	global $formato_foro;\r\n	\r\n	$res = consulta ($sql);\r\n	echo \"	<tr><td>\\n\";\r\n\r\n	if ($formato_foro == \"tabla\" || $es_acceso_administrador) {\r\n		listar_mensajes_tabla ($res, $id_tema, $titulo, $pag, $filtro, $orden, $es_acceso_administrador);\r\n	} else if ($formato_foro== \"lista\") {\r\n		listar_mensajes_lista ($res, $id_tema, $titulo, $pag, $filtro, $orden);\r\n	}\r\n	@mysql_free_result($res);\r\n	echo \"	</td></tr>\\n\";\r\n}\r\n\r\nfunction listar_mensajes_tabla ($res, $id_tema, $titulo, $pag, $filtro, $orden, $es_acceso_administrador)\r\n{\r\n	global $autor_mensaje;\r\n	if (@mysql_num_rows($res)>0) {\r\n	?>\r\n    	<table width=\"100%\" border=\"0\" cellspacing=\"2\" cellpadding=\"1\" class=\"tabla_de_temas\">\r\n          <tr class=\"encabezado_de_temas\">\r\n		  <?php if ($es_acceso_administrador) echo \"            <td align=\\\"center\\\">ID.</td>\\n\"; ?>\r\n            <td>ASUNTO</td>\r\n		  <?php if ($autor_mensaje) { ?>\r\n            <td>AUTOR</td>\r\n		  <?php } ?>\r\n            <td align=\"center\">FECHA</td>\r\n            <td align=\"center\">LECTURAS</td>\r\n            <td align=\"center\">RESPUESTAS</td>\r\n            <td align=\"center\">&Uacute;LTIMA RESP.</td>\r\n			<?php if ($es_acceso_administrador) echo \"            <td align=\\\"center\\\">&nbsp;</td>\\n\"; ?>\r\n          </tr>\r\n	<?php\r\n		while ($reg = @mysql_fetch_array($res)) {\r\n			$id_mensaje = $reg [\'id_mensaje\'];\r\n			$asunto = htmlentities($reg [\'asunto\']);\r\n			$autor = htmlentities($reg [\'usuario\']);\r\n			$fecha_publicacion = $reg [\'fecha_publicacion\'];\r\n			$lecturas = $reg [\'lecturas\'];\r\n			$num_respuestas = $reg [\'num_respuestas\'];\r\n			$fecha_ult_resp = $reg [\'fecha_ultima_respuesta\'];\r\n			echo \"			<tr class=\\\"fila_de_tabla\\\">\\n\";\r\n			if ($es_acceso_administrador) echo \"            <td align=\\\"center\\\">$id_mensaje</td>\\n\";\r\n			if ($es_acceso_administrador)\r\n				echo \"            <td><img src=\\\"../img/folder.gif\\\" alt=\\\"Leer mensaje\\\" width=\\\"15\\\" heigth=\\\"15\\\"> $asunto</td>\\n\";\r\n			else\r\n				echo \"            <td><a href=\\\"mensaje.php?id=$id_tema&id_mensaje=$id_mensaje&titulo=$titulo&pag=$pag&filtro=$filtro&orden=$orden\\\"><img src=\\\"img/folder.gif\\\" alt=\\\"Leer mensaje\\\" width=\\\"15\\\" heigth=\\\"15\\\"> $asunto</a></td>\\n\";\r\n			echo \"            <td>$autor</td>\\n\";\r\n			echo \"            <td align=\\\"center\\\">\".formatear_fecha($fecha_publicacion).\"</td>\\n\";\r\n			echo \"            <td align=\\\"center\\\">$lecturas</td>\\n\";\r\n			echo \"            <td align=\\\"center\\\">$num_respuestas</td>\\n\";\r\n			if ($num_respuestas!=\'0\') echo \"            <td align=\\\"center\\\">\".formatear_fecha($fecha_ult_resp).\"</td>\\n\";\r\n			else echo \"            <td align=\\\"center\\\">No hay respuestas</td>\\n\";\r\n			if ($es_acceso_administrador) echo \"            <td align=\\\"center\\\"><a href=\\\"index.php?borrar=mensaje&id=$id_mensaje&id_tema=$id_tema\\\">Borrar mensaje</a></td>\\n\";\r\n			echo \"          </tr>\\n\";\r\n		}\r\n	} else {\r\n		echo \"<table width=\\\"100%\\\" border=\\\"0\\\" cellspacing=\\\"2\\\" cellpadding=\\\"1\\\" class=\\\"tabla_de_temas\\\"><tr class=\\\"fila_de_tabla\\\"><td>No hay mensajes</td></tr></table>\\n\";\r\n	}\r\n	@mysql_free_result($res);\r\n	echo \"  	</table>\\n\";\r\n}\r\n\r\nfunction listar_mensajes_lista ($res, $id_tema, $titulo, $pag, $filtro, $orden)\r\n{\r\n	global $autor_mensaje;\r\n	//$indice = (($pag -1) * $mensajes_x_pag) + 1;\r\n	if (@mysql_num_rows($res)>0) {\r\n	?>\r\n    	<table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" class=\"tabla_de_temas_lista\">\r\n          <tr class=\"encabezado_de_temas\"> \r\n            <td>ASUNTO</td>\r\n            <td align=\"right\">FECHA</td>\r\n          </tr>\r\n		 </table>\r\n	<?php\r\n		while ($reg = @mysql_fetch_array($res)) {\r\n			$id_mensaje = $reg [\'id_mensaje\'];\r\n			$asunto = htmlentities($reg [\'asunto\']);\r\n			$autor = htmlentities($reg [\'usuario\']);\r\n			$fecha_publicacion = $reg [\'fecha_publicacion\'];\r\n			$lecturas = $reg [\'lecturas\'];\r\n			\r\n			//escribo mensaje y...listo los hijos del nivel 1\r\n			echo \"<table width=\\\"100%\\\" border=\\\"0\\\" cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" class=\\\"tabla_de_temas_lista\\\">\\n\";\r\n			echo \"<tr class=\\\"fila_de_tabla\\\"><td><a href=\\\"mensaje.php?id=$id_tema&id_mensaje=$id_mensaje&titulo=$titulo&pag=$pag&filtro=$filtro&orden=$orden\\\"><img src=\\\"img/folder.gif\\\" alt=\\\"Leer mensaje\\\" width=\\\"15\\\" heigth=\\\"15\\\"> $asunto</a></td>\\n\";\r\n			echo \"<td align=\\\"right\\\">\".formatear_fecha($fecha_publicacion).\"</td></tr>\\n\"; //, por $autor ($lecturas lecturas)\r\n			echo \"</table>\\n\";\r\n			\r\n			$matriz = array(); //Matriz de booleanos que indica donde sigue el camino de directorios\r\n			listar_mensajes_lista_hijos($id_mensaje, $id_tema, $titulo, $pag, 1, $matriz, $filtro, $orden);\r\n		}\r\n	} else {\r\n		echo \"<table width=\\\"100%\\\" border=\\\"0\\\" cellspacing=\\\"2\\\" cellpadding=\\\"1\\\" class=\\\"tabla_de_temas\\\"><tr class=\\\"fila_de_tabla\\\"><td>No hay mensajes</td></tr></table>\\n\";\r\n	}\r\n}\r\n\r\nfunction listar_mensajes_lista_hijos ($id_padre, $id_tema, $titulo, $pag, $nivel, $matriz, $filtro, $orden)\r\n{\r\n	global $autor_mensaje;\r\n	$res = consulta (\"select * from mensajes where id_tema=\'$id_tema\' and id_padre=\'$id_padre\' order by fecha_publicacion\");\r\n	\r\n	if (@mysql_num_rows($res)>0) {\r\n		$indice = 1;\r\n		while ($reg = @mysql_fetch_array($res)) {\r\n			$id_mensaje = $reg [\'id_mensaje\'];\r\n			$asunto = htmlentities($reg [\'asunto\']);\r\n			$autor = htmlentities($reg [\'usuario\']);\r\n			$fecha_publicacion = $reg [\'fecha_publicacion\'];\r\n			$lecturas = $reg [\'lecturas\'];\r\n			\r\n			//escribo mensaje y...listo los hijos del nivel siguiente\r\n			echo \"<table width=\\\"100%\\\" border=\\\"0\\\" cellspacing=\\\"0\\\" cellpadding=\\\"0\\\" class=\\\"tabla_de_temas_lista\\\">\\n\";\r\n			echo \"<tr class=\\\"fila_de_tabla\\\">\\n\";\r\n			\r\n			//Comprobamos si este nivel tiene m·s elementos\r\n			$hay_mas_elementos = @mysql_data_seek($res, $indice++);\r\n			\r\n			//Insertamos los niveles anteriores\r\n			for ($i=0;$i<$nivel-1;$i++) {\r\n				if ($matriz[$i])\r\n					echo \"<td width=\'30\' align=\'center\'><img src=\\\"img/barraI.gif\\\"><img src=\\\"img/blank.gif\\\"></td>\\n\";\r\n				else\r\n					echo \"<td width=\'30\' align=\'center\'>&nbsp;</td>\\n\";\r\n			}\r\n			\r\n			//Escribimos el ˙ltimo nivel o nivel del mensaje actual\r\n			if ($hay_mas_elementos) {\r\n				echo \"<td width=\'30\' align=\'center\'><img src=\\\"img/barraT.gif\\\"><img src=\\\"img/barra-.gif\\\"></td>\\n\";\r\n				$matriz [count($matriz)] = true;\r\n			}\r\n			else {\r\n				echo \"<td width=\'30\' align=\'center\'><img src=\\\"img/barraL.gif\\\"><img src=\\\"img/barra-.gif\\\"></td>\\n\";\r\n				$matriz [count($matriz)] = false;\r\n			}\r\n			\r\n			echo \"<td><a href=\\\"mensaje.php?id=$id_tema&id_mensaje=$id_mensaje&titulo=$titulo&pag=$pag&filtro=$filtro&orden=$orden\\\"><img src=\\\"img/carpeta.gif\\\" alt=\\\"Leer mensaje\\\" width=\\\"15\\\" heigth=\\\"15\\\"> $asunto</a></td>\\n\";\r\n			echo \"<td align=\\\"right\\\">\".formatear_fecha($fecha_publicacion).\"</td></tr>\\n\"; //, por $autor ($lecturas lecturas)\r\n			echo \"</table>\\n\";\r\n			listar_mensajes_lista_hijos($id_mensaje, $id_tema, $titulo, $pag, $nivel+1, $matriz, $filtro, $orden);\r\n		}\r\n	}\r\n}\r\n\r\n/****************************************************\r\n La siguiente funciÛn escribe la tabla formateada de\r\n una consulta del conjunto de temas de los foros.\r\n ****************************************************/\r\nfunction listar_foros($es_acceso_administrador) {\r\n	global $autor_mensaje;\r\n	$res = consulta (\"select * from temas\");\r\n			\r\n	echo \"<tr><td>\\n\";\r\n	if (@mysql_num_rows($res)>0) {\r\n	?>\r\n	<table width=\"100%\" border=\"0\" cellspacing=\"2\" cellpadding=\"1\" class=\"tabla_de_temas\">\r\n          <tr class=\"encabezado_de_temas\">\r\n		    <?php if ($es_acceso_administrador) echo \"            <td align=\\\"center\\\">ID.</td>\\n\"; ?>\r\n            <td>LISTA TEM&Aacute;TICA DE FOROS</td>\r\n			<td>DESCRIPCI&Oacute;N</td>\r\n            <td align=\"center\">MENSAJES</td>\r\n            <td align=\"center\">&Uacute;LTIMO MENSAJE</td>\r\n			<?php if ($es_acceso_administrador) echo \"            <td align=\\\"center\\\">&nbsp;</td>\\n\"; ?>\r\n          </tr>\r\n	<?php\r\n		while ($reg = @mysql_fetch_array($res)) {\r\n			$id_tema = $reg [\'id_tema\'];\r\n			$titulo_tema = $reg [\'titulo_tema\'];\r\n			$descripcion = $reg [\'descripcion\'];\r\n			$sql = \"SELECT usuario, \";\r\n			$sql .= \"fecha_publicacion FROM mensajes WHERE id_tema = \'$id_tema\' ORDER BY fecha_publicacion DESC\"; //AND id_padre=\'0\'\r\n			$res_num_mensajes = consulta ($sql); \r\n			$num_mensajes = @mysql_num_rows($res_num_mensajes);\r\n			if ($num_mensajes>0) {\r\n				$reg2 = @mysql_fetch_array ($res_num_mensajes);\r\n				if ($autor_mensaje)  {\r\n					$ultimo_autor = $reg2[\'usuario\'];\r\n					if (!$ultimo_autor) $ultimo_autor = \"An&oacute;nimo\";\r\n				}\r\n				$fecha_ultima_publicacion = $reg2[\'fecha_publicacion\'];\r\n			}\r\n			@mysql_free_result($res_num_mensajes);\r\n			echo \"			<tr class=\\\"fila_de_tabla\\\">\\n\";\r\n			if ($es_acceso_administrador) echo \"            <td align=\\\"center\\\">$id_tema</td>\\n\";\r\n			if ($es_acceso_administrador)\r\n				echo \"            <td><a href=\\\"index.php?id_tema=$id_tema\\\">\".htmlentities($titulo_tema).\"</a></td>\\n\";\r\n			else\r\n				echo \"            <td><a href=\\\"foro.php?id=$id_tema&titulo=$titulo_tema&pag=1\\\">\".htmlentities($titulo_tema).\"</a></td>\\n\";\r\n			echo \"            <td>\".htmlentities($descripcion).\"</td>\\n\";\r\n			if ($num_mensajes)\r\n				echo \"            <td align=\\\"center\\\">$num_mensajes</td>\\n\";\r\n			else\r\n				echo \"            <td align=\\\"center\\\">0</td>\\n\";\r\n			if ($num_mensajes>0) {\r\n				echo \"            <td align=\\\"center\\\">\".formatear_fecha($fecha_ultima_publicacion);\r\n				if ($autor_mensaje) echo \"<br>escrito por $ultimo_autor\";\r\n				echo \"</td>\\n\";\r\n			}\r\n			else\r\n				echo \"            <td align=\\\"center\\\">No hay mensajes</td>\\n\";\r\n			if ($es_acceso_administrador) echo \"            <td align=\\\"center\\\"><a href=\\\"index.php?borrar=foro&id=$id_tema\\\">borrar foro</a></td></tr>\\n\";\r\n			else echo \"            </tr>\\n\";\r\n		}\r\n		echo \"</table>\\n\";\r\n	} else {\r\n		echo \"<table width=\\\"100%\\\" border=\\\"0\\\" cellspacing=\\\"2\\\" cellpadding=\\\"1\\\" class=\\\"tabla_de_temas\\\"><tr class=\\\"fila_de_tabla\\\"><td>No hay temas de discusiÛn</td></tr></table>\\n\";\r\n	}\r\n	@mysql_free_result($res);\r\n	echo \"</td></tr>\\n\";\r\n}\r\n\r\n/****************************************************\r\n La siguiente funciÛn escribe la fila de la paginaciÛn\r\n ****************************************************/\r\nfunction crear_paginacion($limite_inf, $limite_sup, $num_paginas, $num_mensajes_total, $id_tema, $titulo, $pag, $filtro, $orden){\r\n	echo \"	<tr><td align=\\\"center\\\" class=\\\"fila_de_tabla\\\">\\n\";\r\n	if ($num_paginas>1) {\r\n		if ($pag==$num_paginas) $limite_sup = $num_mensajes_total;\r\n		echo \"Mensajes del \".($limite_inf+1).\" al $limite_sup ($num_mensajes_total en total). Este foro tiene $num_paginas p&aacute;ginas&nbsp;&nbsp;&nbsp;\\n\";\r\n		if ($pag!=\"1\") {\r\n			$izq = $pag - 1;\r\n			//echo \"<font class=\\\"valor\\\" onClick=\\\"document.location=\'foro.php?id=$id_tema&titulo=$titulo&pag=$izq\'\\\" onMouseOver=\\\"this.style.cursor = \'hand\';\\\">&lt;&lt;</font>&nbsp;\\n\";\r\n			echo \"<a href=\\\"foro.php?id=$id_tema&titulo=$titulo&pag=$izq&filtro=$filtro&orden=$orden\\\">&lt;&lt;</a>&nbsp;\\n\";\r\n		}\r\n		for ($i=1;$i<=$num_paginas;$i++) {\r\n			if ($i==$pag)\r\n				echo \" <font class=\\\"campo\\\">[$i]</font> &nbsp;\";\r\n			else\r\n				//echo \" <font class=\\\"valor\\\" onClick=\\\"document.location=\'foro.php?id=$id_tema&titulo=$titulo&pag=$i\'\\\" onMouseOver=\\\"this.style.cursor = \'hand\';\\\">[$i]</font> &nbsp;\";\r\n				echo \" <a href=\\\"foro.php?id=$id_tema&titulo=$titulo&pag=$i&filtro=$filtro&orden=$orden\\\">[$i]</a> &nbsp;\";\r\n		}\r\n		if ($pag!=$num_paginas) {\r\n			$der = $pag + 1;\r\n			//echo \"&nbsp;<font class=\\\"valor\\\" onClick=\\\"document.location=\'foro.php?id=$id_tema&titulo=$titulo&pag=$der\'\\\" onMouseOver=\\\"this.style.cursor = \'hand\';\\\">&gt;&gt;</font>\\n\";\r\n			echo \"<a href=\\\"foro.php?id=$id_tema&titulo=$titulo&pag=$der&filtro=$filtro&orden=$orden\\\">&gt;&gt;</a>&nbsp;\\n\";\r\n		}\r\n	}\r\n	echo \"	</td></tr>\\n\";\r\n}\r\n\r\nfunction filtroYordenacion($filtro, $orden) {\r\n	//FILTRADO\r\n	if ($filtro == \"hoy\") {\r\n		$fecha_hoy = date(\"Y-m-d 00:00:00\");\r\n		$filtro = \"AND fecha_publicacion>=\'$fecha_hoy\'\";\r\n	}\r\n	else if ($filtro == \"semana\") {\r\n		$fecha_semana = date(\"Y-m-d H:i:s\",time()-604800); \r\n		$filtro = \"AND fecha_publicacion>=\'$fecha_semana\'\";\r\n	}\r\n	else if ($filtro == \"mes\") {\r\n		if (date(\"m\")>\"10\")\r\n			$fecha_mes = date(\"Y-\").(date(\"m\")-1).date(\"-d H:i:s\");\r\n		else if (date(\"m\")>\"01\")\r\n			$fecha_mes = date(\"Y-0\").(date(\"m\")-1).date(\"-d H:i:s\");\r\n		else\r\n			$fecha_mes = (date(\"Y\")-1).date(\"-12-d H:i:s\");\r\n		$filtro = \"AND fecha_publicacion>=\'$fecha_mes\'\";\r\n	}\r\n	else if ($filtro == \"anyo\")\r\n		$filtro = \"AND fecha_publicacion>=\'\".(date(\"Y\")-1).date(\"-m-d H:i:s\").\"\'\";\r\n	else\r\n		$filtro = \"\";\r\n\r\n	//ORDENACI”N\r\n	if ($orden == \"asunto\")\r\n		$orden = \"order by asunto\";\r\n	else if ($orden == \"autor\")\r\n		$orden = \"order by usuario\";\r\n	else if ($orden == \"lecturas\")\r\n		$orden= \"order by lecturas DESC\";\r\n	else if ($orden == \"respuestas\")\r\n		$orden= \"order by num_respuestas DESC\";\r\n	else if ($orden == \"ultima_resp\")\r\n		$orden= \"order by fecha_ultima_respuesta DESC\";\r\n	else\r\n		$orden = \"order by fecha_publicacion DESC\";\r\n		\r\n	$tabla [\"filtro\"] = $filtro;\r\n	$tabla [\"orden\"] = $orden;\r\n	\r\n	return $tabla;\r\n}\r\n\r\n/****************************************************\r\n La siguiente funciÛn escribe el encabezado de p·gina\r\n ****************************************************/\r\nfunction imprime_encabezado_de_pagina($titulo, $javascript, $es_acceso_administrador) {\r\n	echo \"<html>\\n\\n\";\r\n	echo \"<head>\\n\";\r\n	echo \"<title>$titulo</title>\\n\";\r\n	echo \"<meta http-equiv=\\\"Content-Type\\\" content=\\\"text/html; charset=iso-8859-1\\\">\\n\";\r\n	if ($es_acceso_administrador)\r\n		echo \"<link rel=\\\"stylesheet\\\" href=\\\"../estiloForo.css\\\" type=\\\"text/css\\\">\\n\";\r\n	else\r\n		echo \"<link rel=\\\"stylesheet\\\" href=\\\"estiloForo.css\\\" type=\\\"text/css\\\">\\n\";\r\n	if ($javascript) echo $javascript;\r\n	echo \"</head>\\n\\n\";\r\n\r\n	echo \"<body>\\n\";\r\n	echo \"<center>\\n\";\r\n	echo \"<table width=\\\"100%\\\" class=\\\"tabla_de_foro\\\" cellspacing=\\\"2\\\">\\n\";\r\n	//echo \"<tr><td colspan=\\\"2\\\" class=\\\"encabezado_de_foro\\\">FORO P&Uacute;BLICO</td></tr>\\n\";\r\n}\r\n\r\n/*********************************************\r\n La siguiente funciÛn escribe el pie de p·gina\r\n *********************************************/\r\nfunction imprime_pie_de_pagina() {\r\n	echo \"</table>\\n\";\r\n	echo \"</center>\\n\";\r\n	echo \"</body>\\n\";\r\n	echo \"</html>\\n\";\r\n}\r\n?>", "includes/funciones.inc.php", "1");
INSERT INTO ficheros VALUES("61", "foro protegido", "<!--\r\n//Valida un mail, comprobando que posea los caracteres necesarios\r\nfunction validarEmail(mail)\r\n{\r\n	if (mail==\"\" || ( mail.indexOf (\'@\', 0) != -1 && mail.indexOf (\'.\', 0) != -1 ))\r\n		return (true);\r\n	else {\r\n		alert(\"Escriba una direcciÛn de correo v·lida.\"); \r\n    	return (false);\r\n	}\r\n}\r\n\r\n//-->", "includes/funciones.js", "9");
INSERT INTO ficheros VALUES("62", "foro protegido", "<?php\r\n\r\n/***********************************\r\n MÛdulo para la creaciÛn de claves\r\n aleatorias para usar para accesos.\r\n \r\n Recibe dos par·metros que indican\r\n el mÌnimo y el m·ximo de caracteres\r\n que puede tener la clave.\r\n ***********************************/\r\nfunction crear_clave_aleatoria ($tam_key_min, $tam_key_max) {\r\n\r\n	list($usec, $sec) = explode(\' \', microtime());\r\n	srand( (float) $sec + ((float) $usec * 100000) );\r\n\r\n	// Generamos la clave\r\n	$clave=\"\";\r\n	\r\n	//La clave la definimos entre el tamaÒo mÌnimo y m·ximo\r\n	$max_chars = round( rand($tam_key_min, $tam_key_max) );\r\n	$chars = array();\r\n	//Creamos un vector con letras\r\n	for ($i=\"a\"; $i<=\"z\"; $i++)\r\n		$chars[] = $i;\r\n	\r\n	//Generamos la clave de max_chars caracteres con valores aleatorios de letras y n˙meros\r\n	for ($i=0; $i<$max_chars; $i++) {\r\n	  //Elegimos aleatoriamente si escribimos un n˙mero o una letra en la posiciÛn del car·cter actual\r\n	  $letra = round(rand(0, 1));\r\n	  \r\n	  if ($letra) //Escogemos una letra\r\n		$clave .= $chars[round(rand(0, count($chars)-1))];\r\n	  else //Escogemos un n˙mero\r\n		$clave .= round(rand(0, 9));\r\n	}\r\n	return $clave;\r\n}\r\n?>\r\n", "includes/mod_claves.inc.php", "1");
INSERT INTO ficheros VALUES("63", "foro protegido", "<?php\r\n/*\r\n    //CREACI”N DE LA TABLA DE USUARIOS\r\n    consulta (\"CREATE TABLE usuariosConectados (\r\n        tiempoConexion int(15) DEFAULT \'0\' NOT NULL,\r\n        ip varchar(40) NOT NULL,\r\n        archivo varchar(100) NOT NULL,\r\n        INDEX (tiempoConexion),\r\n        INDEX ip(ip),\r\n        INDEX archivo(archivo)\r\n       )\");\r\n*/\r\n    \r\nclass usuariosConectados {\r\n\r\n    var $tiempoEnSegundosSinConexion = 120;\r\n    var $num_usuarios = 0;\r\n    \r\n    function usuariosConectados() {\r\n        $this->refrescar();                                                                               \r\n    }\r\n    \r\n    function get_num_usuarios() {\r\n        return $this->num_usuarios;\r\n    }\r\n    \r\n    function refrescar() {\r\n        global $REMOTE_ADDR, $PHP_SELF;\r\n        \r\n        $tiempoActual = time();\r\n        $tiempoSinConexion = $tiempoActual - $this->tiempoEnSegundosSinConexion;\r\n        \r\n        /*mysql_connect($this->host, $this->user, $this->password)\r\n            or die(\'Error conecting to database\');*/\r\n            \r\n        consulta(/*$this->database,*/\r\n                       \"INSERT INTO usuariosConectados VALUES (\'$tiempoActual\',\'$REMOTE_ADDR\',\'$PHP_SELF\')\") \r\n            or die(\'Error writing to database\');                       \r\n            \r\n        consulta(/*$this->database,*/\r\n                       \"DELETE FROM usuariosConectados WHERE tiempoConexion < $tiempoSinConexion\")\r\n            or die(\'Error deleting from database\');\r\n            \r\n        $result = consulta(/*$this->database,*/\r\n                                 \"SELECT DISTINCT ip FROM usuariosConectados WHERE archivo=\'$PHP_SELF\'\")\r\n            or die(\'Error reading from database\');\r\n\r\n        $this->num_usuarios = mysql_num_rows($result); \r\n                                                                                     \r\n        /*mysql_close();*/\r\n        @mysql_free_result($result);\r\n    }\r\n    \r\n}\r\n\r\n?>", "includes/usuariosConectados.class.inc.php", "1");
INSERT INTO ficheros VALUES("64", "foro protegido", "<?php\r\n	@session_start();\r\n	\r\n	include \"includes/funciones.inc.php\";\r\n	include \"includes/config.inc.php\";\r\n	\r\n	global $permitir_filtro_foro;\r\n	\r\n	$id_tema = @$HTTP_GET_VARS[\'id\'];\r\n	$titulo = @$HTTP_GET_VARS[\'titulo\'];\r\n	$busqueda = @$HTTP_POST_VARS[\'busqueda\'];\r\n	$filtro = @$HTTP_POST_VARS[\'filtro\'];\r\n	$pag = @$HTTP_GET_VARS[\'pag\'];\r\n	\r\n	if ($permitir_filtro_foro) {\r\n		$filtrar = @$HTTP_GET_VARS[\'filtro\'];\r\n		$orden = @$HTTP_GET_VARS[\'orden\'];\r\n	}\r\n	\r\n	if (!$pag) $pag = 1;\r\n	\r\n	imprime_encabezado_de_pagina (\"Busqueda en el foro $titulo\",\"\", false);\r\n	\r\n	if ($permitir_iconos_como_enlaces)\r\n		$tam_enlaces = 50;\r\n	else\r\n		$tam_enlaces = 200;\r\n?>\r\n<!-- Principio de la fila de enlaces -->\r\n    <tr><td class=\"encabezado_de_foro\"> <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n          <tr> \r\n            <td class=\"encabezado_de_foro\">\r\n              <?=$titulo?>\r\n            </td>\r\n            <td width=\"<?=$tam_enlaces?>\"> <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n                <tr> \r\n				<?php if ($permitir_iconos_como_enlaces) { ?>\r\n                  	<td><a href=\"foro.php?id=<?=$id_tema;?>&titulo=<?=$titulo?>&pag=<?=$pag?>&filtro=<?=$filtrar?>&orden=<?=$orden?>\"><img src=\"img/volver1.gif\" alt=\" Volver al foro\" width=\"20\" height=\"20\"></a></td>\r\n                  	<td align=\"right\"><a href=\'buscar.php?id=<?=$id_tema?>&titulo=<?=$titulo?>&pag=<?=$pag?>&filtro=<?=$filtrar?>&orden=<?=$orden?>\'><img src=\"img/buscar.gif\" alt=\"Nueva b&uacute;squeda\" width=\"20\" height=\"20\"></a></td>\r\n				<?php } else { ?>\r\n					<td><a href=\"foro.php?id=<?=$id_tema;?>&titulo=<?=$titulo?>&pag=<?=$pag?>&filtro=<?=$filtrar?>&orden=<?=$orden?>\">Volver al foro</a></td>\r\n                  	<td align=\"right\"><a href=\'buscar.php?id=<?=$id_tema?>&titulo=<?=$titulo?>&pag=<?=$pag?>&filtro=<?=$filtrar?>&orden=<?=$orden?>\'>Nueva b&uacute;squeda</a></td>\r\n				<?php } ?>\r\n                </tr>\r\n              </table></td>\r\n          </tr>\r\n        </table></td>\r\n    </tr>\r\n<!-- Fin de la fila de enlaces -->\r\n<!-- Principio de la fila con la TABLA DE B⁄SQUEDA -->\r\n    <tr>\r\n      <td>\r\n          <table width=\"100%\" border=\"0\" cellspacing=\"5\" cellpadding=\"0\" class=\"tabla_de_temas\">\r\n            <tr> \r\n              <td><form method=\"POST\" action=\"<?=$PHP_SELF;?>?id=<?=$id_tema;?>&titulo=<?=$titulo?>&pag=<?=$pag?>&filtro=<?=$filtrar?>&orden=<?=$orden?>\" enctype=\"multipart/form-data\"><br>\r\n              	<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"400\" class=\"tabla_de_temas\" align=\"center\">\r\n                  <tr> \r\n                    <td height=\"25\"><b> &nbsp;&nbsp;Escriba la cadena a buscar: </b></td>\r\n                  </tr>\r\n                  <tr> \r\n                    <td height=\"60\" valign=\"middle\" align=\"center\">\r\n                    <input type=\"text\" name=\"busqueda\" size=\"30\" maxlength=\"50\" value=\"<?=$busqueda;?>\">\r\n                    &nbsp; \r\n                    <input type=\"submit\" value=\"Buscar en el foro\" name=\"buscar\" class=\"boton\"><br>\r\n                    <input type=\"radio\" name=\"filtro\" value=\"asunto\" <?php if (!$filtro || $filtro==\'asunto\') echo \"checked\"; ?>>\r\n                    En el asunto\r\n                    <input type=\"radio\" name=\"filtro\" value=\"descripcion\"<?php if ($filtro==\'descripcion\') echo \"checked\"; ?>>\r\n                    En la descripci&oacute;n\r\n                    <input type=\"radio\" name=\"filtro\" value=\"ambos\"<?php if ($filtro==\'ambos\') echo \"checked\"; ?>>\r\n                    En ambos</td>\r\n                  </tr>\r\n                </table></form></td>\r\n            </tr>\r\n          </table>\r\n        </td>\r\n    </tr>\r\n	<tr><td>\r\n<!-- Fin de la fila con la TABLA DE B⁄SQUEDA -->\r\n<?php\r\n	//Realizamos la b˙squeda en su caso y presentamos los resultados\r\n	if ($busqueda) {\r\n		//REALIZAMOS LA B⁄SQUEDA S”LO EN MAY⁄SCULAS\r\n		$busqueda_may = strtoupper($busqueda);\r\n		\r\n		//FILTRAMOS LA B⁄SQUEDA POR ASUNTO O DESCRIPCI”N, EN SU CASO\r\n		if ($filtro==\"asunto\")\r\n			$filtrar_por = \"asunto LIKE \'%$busqueda_may%\'\";\r\n		else if ($filtro==\"descripcion\")\r\n			$filtrar_por = \"descripcion LIKE \'%$busqueda_may%\'\";\r\n		else\r\n			$filtrar_por = \"asunto LIKE \'%$busqueda_may%\' OR descripcion LIKE \'%$busqueda_may%\'\";\r\n	\r\n		//CREAMOS LA SENTENCIA SQL DE LA B⁄SQUEDA Y MOSTRAMOS LOS RESULTADOS\r\n		$sql = \"SELECT id_mensaje, asunto, usuario, fecha_publicacion, lecturas, num_respuestas, fecha_ultima_respuesta \r\n				FROM mensajes WHERE id_tema=\'$id_tema\' \r\n				AND ($filtrar_por) \r\n				ORDER BY fecha_publicacion DESC LIMIT 0, 10\";\r\n				\r\n		//Listamos los resultados de la b˙squeda\r\n		listar_mensajes($sql, $id_tema, $titulo, $pag, $filtro, $orden, false);\r\n	}\r\n\r\n	//Pie de p·gina\r\n	imprime_pie_de_pagina();\r\n?>         ", "buscar.php", "1");
INSERT INTO ficheros VALUES("65", "foro protegido", "<?php\r\nsession_start();\r\ninclude \"includes/funciones.inc.php\";\r\n$sql = \"SELECT imagen_data,imagen_type,imagen_name FROM mensajes WHERE id_mensaje=\'\".@$HTTP_GET_VARS[\'id_mensaje\'].\"\'\";\r\n$res=@consulta($sql);\r\nif ($fila=@mysql_fetch_array($res)) {\r\n   $data = @$fila[\'imagen_data\'];\r\n   $type = @$fila[\'imagen_type\'];\r\n   $filename = @$fila[\'imagen_name\'];\r\n   \r\n   //Introducimos las cabeceras\r\n   header(\"Content-Disposition: atachment; filename=$filename\");\r\n   header(\"Cache-Control: no-store, no-cache, must-revalidate, post-check=0, pre-check=0\"); \r\n   header(\"Pragma: no-cache\");\r\n   header(\"Expires: 0\");\r\n   if (@$HTTP_GET_VARS[\'ver\']==\'no\')\r\n   		header(\"Content-Type: application/force-download\"); //lanzamos una cabecera inexistente para forzar download\r\n   else\r\n   		header(\"Content-type: \".$type);\r\n   header(\"Content-Transfer-Encoding: binary\");\r\n   \r\n   echo $data; //presenta la imagen\r\n}\r\n?>\r\n", "consultaImagen.php", "1");
INSERT INTO ficheros VALUES("66", "foro protegido", "<?php\r\n	@session_start();\r\n	\r\n	include \"includes/funciones.inc.php\";\r\n	include \"includes/config.inc.php\";\r\n	\r\n	global $autor_mensaje, $email_mensaje, $web_mensaje, $imagen_mensaje, $permitir_buscador_foro;\r\n	\r\n	if (!session_is_registered(\'login_usuario\')) {\r\n		echo \"<link rel=\\\"stylesheet\\\" href=\\\"estiloForo.css\\\" type=\\\"text/css\\\"><body></body>\\n\";\r\n		echo \"<script language=\\\"javascript\\\">\\n\";\r\n		echo \"	alert (\'Es necesario estar registrado para escribir en el foro.\');\";\r\n		echo \"	history.back();\\n\";\r\n		echo \"</script>\\n\";\r\n		exit;\r\n	}\r\n		\r\n	$id_tema = @$HTTP_GET_VARS[\'id\'];\r\n	$titulo = @$HTTP_GET_VARS[\'titulo\'];\r\n	$padre = @$HTTP_GET_VARS[\'padre\'];\r\n	$asunto = @$HTTP_GET_VARS[\'asunto\'];\r\n	$pag = @$HTTP_GET_VARS[\'pag\'];\r\n	if (!$pag) $pag=1;\r\n	\r\n	//Si es una respuesta a un mensaje le aÒadimos el encabezado de respuesta al asunto\r\n	if ($asunto)\r\n		$asunto = \"RE: \".$asunto;\r\n	\r\n	//Si se ha realizado submit grabamos los datos del nuevo mensaje en la base de datos\r\n	if (@$HTTP_POST_VARS) {\r\n		//Recogemos los datos del formulario	\r\n		$asunto = @$HTTP_POST_VARS[\'asunto\'];\r\n		$id_usuario = @$_SESSION[\'login_usuario\'];\r\n		$descripcion = @$HTTP_POST_VARS[\'descripcion\'];\r\n		\r\n		//Datos de la imagen\r\n		if ($imagen_mensaje) {\r\n			$imagen_type=$HTTP_POST_FILES[\'imagen\'][\'type\'];\r\n			$imagen_name=$HTTP_POST_FILES[\'imagen\'][\'name\'];\r\n			$tmp_data=$HTTP_POST_FILES[\'imagen\'][\'tmp_name\'];\r\n			$filesize=$HTTP_POST_FILES[\'imagen\'][\'size\'];\r\n			$imagen_data = @addslashes(fread(fopen($tmp_data, \"rb\"), $filesize));\r\n		}\r\n		\r\n		//Datos auxiliares\r\n		$fecha = date(\"Y-m-d H:i:s\"); //Obtiene la fecha actual\r\n		$autor_host = gethostbyaddr(\"$REMOTE_ADDR\"); //Obtiene la IP del autor del mensaje\r\n		\r\n		//Creamos la sentencia sql y la ejecutamos para dar de alta el nuevo mensaje\r\n		$sql = \"INSERT INTO mensajes (id_tema, id_padre, asunto, usuario, \";\r\n		if ($imagen_mensaje) $sql .= \"imagen_data, imagen_name, imagen_type, \";\r\n		$sql .= \"autor_host, descripcion, fecha_publicacion) VALUES (\'$id_tema\', \'$padre\', \'$asunto\', \'$id_usuario\', \";\r\n		if ($imagen_mensaje) $sql .= \"\'$imagen_data\', \'$imagen_name\', \'$imagen_type\', \";\r\n		$sql .= \"\'$autor_host\', \'$descripcion\', \'$fecha\')\";\r\n		consulta ($sql);\r\n\r\n		//Ahora creamos la sentencia para actualizar al padre, en su caso\r\n		if ($padre!=\'0\') {\r\n			$sql = \"UPDATE mensajes SET num_respuestas=num_respuestas+1, fecha_ultima_respuesta=\'$fecha\' WHERE id_mensaje=\'$padre\' AND id_tema=\'$id_tema\'\";\r\n			consulta ($sql);\r\n		}\r\n		\r\n		header (\"Location: foro.php?id=$id_tema&titulo=$titulo&pag=$pag\");\r\n		exit;\r\n	}\r\n	$javascript = \"\r\n<script language=\\\"JavaScript\\\" src=\\\"includes/funciones.js\\\" type=\\\"text/javascript\\\"></script>\r\n<script language=\\\"JavaScript\\\" type=\\\"text/javascript\\\">\r\n<!--\r\nfunction comprobarFormulario(form) {\r\n	//Comprueba si se ha introducido asunto\r\n	if (form.asunto.value==\\\"\\\") {\r\n		alert (\\\"No puedes dejar el asunto vacÌo\\\");\r\n		form.asunto.focus();\r\n		return (false);\r\n	}\r\n\r\n	//Comprueba si se ha introducido la descripciÛn\r\n	if (form.descripcion.value==\\\"\\\") {\r\n		alert (\\\"No puedes dejar la descripciÛn del mensaje vacÌa\\\");\r\n		form.descripcion.focus();\r\n		return (false);\r\n	}\r\n	\";\r\n	\r\nif ($imagen_mensaje) {\r\n	$javascript .= \"\r\n	//Comprueba si la imagen introducida es una jpg o una gif\r\n	if (form.imagen.value!=\\\"\\\") {\r\n		var dir = new String(form.imagen.value);\r\n	    var long = dir.length;\r\n		\r\n		dir = dir.substr(long-3,3);\r\n	    dir = dir.toLowerCase();\r\n		\r\n		if ((dir!=\'gif\' && dir!=\'jpg\')){\r\n			alert(\\\"La imagen seleccionada no es jpg o gif.\\\");\r\n			form.imagen.focus();\r\n			return (false);\r\n		}\r\n	}\r\n	\";\r\n}\r\n$javascript .= \"\r\n	form.submit();\r\n	return (true);\r\n}\r\n//-->\r\n</script>\r\n\";\r\n	\r\n	//Encabezado de p·gina\r\n	imprime_encabezado_de_pagina(\"Escribir un mensaje en el foro $titulo\", $javascript, false);\r\n	\r\n	if ($permitir_iconos_como_enlaces)\r\n		$tam_enlaces = 50;\r\n	else\r\n		$tam_enlaces = 150;\r\n?>\r\n<!-- Principio de la fila de enlaces -->\r\n    <tr> \r\n      <td class=\"encabezado_de_foro\"> <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n          <tr> \r\n            <td class=\"encabezado_de_foro\">\r\n              <?=$titulo?>\r\n            </td>\r\n            <td width=\"<?=$tam_enlaces?>\"> <table width=\"100%\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n                <tr> \r\n				<?php if ($permitir_iconos_como_enlaces) { ?>\r\n					  <td align=\"<?php if ($permitir_buscador_foro) echo \"center\"; else echo \"right\"; ?>\"><a href=\"javascript:history.back();\"><img src=\"img/volver1.gif\" alt=\"Volver atr&aacute;s\" width=\"20\" height=\"20\"></a></td>\r\n					  <!-- foro.php?id=<?=$id_tema;?>&titulo=<?=$titulo?> -->\r\n					  <?php if ($permitir_buscador_foro) { ?>\r\n					  <td align=\"right\"><a href=\'buscar.php?id=<?=$id_tema?>&titulo=<?=$titulo?>&pag=<?=$pag?>\'><img src=\"img/buscar.gif\" alt=\"Buscar\" width=\"20\" height=\"20\"></a></td>\r\n					  <?php } ?>\r\n				 <?php } else { ?>\r\n					  <td align=\"<?php if ($permitir_buscador_foro) echo \"center\"; else echo \"right\"; ?>\"><a href=\"javascript:history.back();\">Volver atr&aacute;s</a></td>\r\n					  <!-- foro.php?id=<?=$id_tema;?>&titulo=<?=$titulo?> -->\r\n					  <?php if ($permitir_buscador_foro) { ?>\r\n					  <td align=\"right\"><a href=\'buscar.php?id=<?=$id_tema?>&titulo=<?=$titulo?>&pag=<?=$pag?>\'>Buscar</a></td>\r\n					  <?php } ?>\r\n				 <?php } ?>\r\n                </tr>\r\n              </table></td>\r\n          </tr>\r\n        </table></td>\r\n    </tr>\r\n<!-- Fin de la fila de enlaces -->\r\n<!-- Principio de la fila con el FORMULARIO DE ENVÕO -->\r\n    <tr>\r\n      <td><form name=\"mensaje\" method=\"POST\" action=\"<?=$PHP_SELF;?>?id=<?=$id_tema;?>&titulo=<?=$titulo?>&padre=<?=$padre?>&pag=<?=$pag?>\" enctype=\"multipart/form-data\">\r\n          <table width=\"100%\" border=\"0\" cellspacing=\"5\" cellpadding=\"0\" class=\"tabla_de_temas\">\r\n            <tr> \r\n              <td><table width=\"100%\" border=\"0\" cellspacing=\"5\" cellpadding=\"0\">\r\n              <tr> \r\n                <td width=\"30%\" class=\"campo_oblig\"><div align=\"right\">Asunto: \r\n                  </div></td>\r\n                <td width=\"70%\"><input name=\"asunto\" type=\"text\" class=\"datos_form\" size=\"50\" maxlength=\"255\" value=\"<?=$asunto?>\" onBlur=\"this.value=this.value.toUpperCase();\"></td>\r\n              </tr>\r\n              <tr> \r\n                <td><div align=\"right\"><font class=\"campo_oblig\">Descripci&oacute;n: \r\n                    </font></div></td>\r\n                <td><textarea name=\"descripcion\" cols=\"50\" rows=\"5\" class=\"datos_form\" onBlur=\"this.value=this.value.toUpperCase();\"></textarea>\r\n					<?php if ($permitir_codigo_html) echo \"<br><font class=\\\"valor\\\">(Se puede escribir c&oacute;digo html si se desea)</font>\"; ?>\r\n					</td>\r\n              </tr>\r\n              <tr> \r\n                <td><div align=\"right\"></div></td>\r\n                <td>&nbsp;</td>\r\n              </tr>\r\n              <tr> \r\n                <?php if ($imagen_mensaje) {?>\r\n                <td><div align=\"right\"><font class=\"campo\">Imagen relacionada: \r\n                    </font></div></td>\r\n                <td><input name=\"imagen\" type=\"file\" class=\"datos_form\" size=\"50\" accept=\"image/jpeg,image/gif\"></td>\r\n              </tr>\r\n              <tr> \r\n                <td colspan=\"2\">&nbsp;</td>\r\n              </tr>\r\n              <?php }?>\r\n              <tr> \r\n  